import React from 'react';
import classes from './Modal.css'

const modal = (props) => {
    return (
        <div className={classes.Modal}
            style={{ transform: props.show ? 'scale(1)' : 'scale(0)',
                     opacity: props.show ? '1' : '0'}}
            >
            { props.children}
        </div>
    );
}

export default modal;
