import React, {useEffect,Component} from 'react';
import Aux from '../../../Hoc/Aux';
import Button from '../../UI/Button/Button';

class OrderSummary extends Component {

    // useEffect(() => {
    //     console.log('order');
    // })
    component() {
        console.log('order');
    }
     orderSummary = Object.keys(this.props.ingredients)
            .map(ingredientKey => {
                return <li key={ingredientKey}>
                            <span style={{ textTransform: 'capitaliaze' }}>{ingredientKey}</span>: {this.props.ingredients[ingredientKey]}
                        </li>
            });
            render() {
                return (
                    <Aux>
                        <h3>Your Order</h3>
                        <p>A delicious burger with following ingredients:</p>
                        <ul>
                            {this.orderSummary}
                        </ul>
                        <p><strong>Total Price: {this.props.price}</strong></p>
                        <p>Continue to Checkout?</p>
                        <Button buttonType='Success' clicked={this.props.continuePurchase}>Continue</Button>
                        <Button buttonType='Danger' clicked={this.props.cancelPurchase}>Cancel</Button>
                    </Aux>
                );
            }

}

export default OrderSummary;
